import '../Home.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import { Collapse, CardBody, Card  } from "reactstrap";

import '../AppFaq.css';

import img from "../images/img.png";
import N from "../images/Frame.png";
//import FaqImg from "../images/FaqImg.png";
//import faqAmarelo from "../images/faqAmarelo.png";
import Vector from "../images/idioma.png";



import CetaBaixo from "../images/Vector.png";

function GoTokensNft(){
    return (
        <div class="container"  >
          <div class="row">
          
            <div class="col-lg-4 col-md-12 col-sm-12">
              <div id="MaskGroup">
              
                <div className="GoTokens"><h2 style={{fontWeight:"bold"}}>Go Tokens</h2></div>
                <div className="NFT">
               

                <img  src={N}class="img-fluid" style={{}} alt=""/>
              
                </div>
                <div className="paragrafo"><p>Seu espa√ßo pessoal de cria√ß√£o e capitaliza√ß√£o de col√©cion√°veis para todo o p√∫blico</p></div>
                <div className="botao">
                    <a href="/GofundNFT">
                      <button id="botaoComecar">Come√ßar</button>
                    </a> 
                </div>
                <div className="RoleAbaixo">
                    <p className="gradient">ROLE ABAIXO PARA SABER MAIS</p>
                    <img  src={CetaBaixo}class="img-fluid" style={{height:"18px", width:"17px",marginLeft:"227px",marginTop:"-65px"}} alt=""/>
                </div>
              </div>
            </div>
            <div class="col-lg-1 col-md-0 col-sm-0">
            </div> 



            <div class="col-lg-6 col-md-12 col-sm-12"> 
              <div id="MaskGroupLdireito" >             
                  <img id="MaskGroupLdireitoImg"
                        style={{ marginTop: "0px", marginLeft: "0px" }}
                        src={img}
                        class="img-fluid"
                        alt="Gotokens"
                        />
                </div>   
				<BotaoFaq/>
            </div>
            
          </div>
        </div>
        
    );
}

export default GoTokensNft;
/* O CSS DA FAQ ESTA EM UMA PASTA SEPADARA CHAMADA APPFAQ.CSS */

function BotaoFaq() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<div class="container" id="Espa√ßoTelagrande" >
		  <div class="col-12">

        <button className="buttonFaq" onClick={toggle} style={{ }}>
		  <div id="fig">
			  <img alt="" style={{marginTop:'-10px'}} />
			 
		  </div>
        </button>
				
				
			
			
	<Collapse isOpen={isOpen} >
		<Card id="CardStyle">
			<CardBody id="CArdBodyStyle" >
              <h4 style={{color:"#C8C8C8", textAlign:"center", marginTop:"40px"}}>N√£o Tenha D√∫vidas</h4>
              <h2 style={{color:"#717171", textAlign:"center"}}>Perguntas Frequentes</h2>
              <OqEtoken/>
              <Tokeniza√ßao/>
              <AtivosDigitaisl/>
              <OqEnFT/>
              <OqAtivoNfungivel/>
              <QuemEmitirToken/>
              <GoEmissorTokens/>
              <PqEmitirToken/>
              <SeguroComprarToken/>
              <PqBlockchainSeguro/>
              <ExisteRiscoComprarToken/>
              <OndeGuardadoToken/>
              <OqUmaWallet/>
              <QualDiferencaWallet/>
              <PqUsaraGoWallet/>
              <OndEcriadoToken/>
              <QuaisTiposTokens/>
              <ElegalEmitirTokens/>
              <QualVantagemCriarToken/>
              <OqEgoTokens/>
              <QueroEmitirUmToken/>
              <QuantoTempoCriarToken/>
              <QtipoTokensPodemSerCriados/>
              <OquePrecisoCriarProprioToken/>
              <OqEtokenEconomics/>
              <QuantoCustaPcriarMeuToken/>
              <ExisteCustoPcompradorToken/>
              <OqueFazUmBanco/>
              <QuaisServi√ßosUmBancoOferece/>
			</CardBody>					
		</Card>
	</Collapse>
	</div>		
</div>		
	);
}

function Tokeniza√ßao() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
				<button className="faqbutton" onClick={toggle} style={{ marginBottom: "1rem"}}>
					<h5 style={{marginTop:'20px'}}>O que √© tokeniza√ß√£o?</h5>
				</button>
				<img onClick={toggle} style={{ cursor:'pointer'}} alt=""src={Vector}></img>
				
				<Collapse isOpen={isOpen}>
					<Card style={{boxShadow:'none', borderRadius:'20px', border:'none', height:'100%'}}>
						<CardBody>
							<p>√â transformar um ativo f√≠sico em formato digital, atrav√©s de um processo conhecido como ‚ÄúTokeniza√ß√£o‚Äù. O emissor dos tokens pode dividir um ativo em parcelas menores, aumentando sua liquidez ao poder movimentar fra√ß√µes do ativo. J√° o detentor dos tokens conta com a seguran√ßa, transpar√™ncia e autonomia conferida pela tecnologia utilizada na tokeniza√ß√£o, a blockchain (banco de dados descentralizado). Por conta das caracter√≠sticas de funcionamento da Blockchain, os processos de transfer√™ncia e o registro da posse de um token s√£o simplificados.</p>
							
							<p>	Esta √© uma tend√™ncia e j√° existem muitos ativos sendo negociados em forma de tokens. Um exemplo recente s√£o os NFT - Non fungible tokens que representam obras de arte digitais. √â poss√≠vel tokenizar um im√≥vel, um t√≠tulo precat√≥rio e negoci√°-los.</p>

							<p>Uma outra prova de sucesso da estrat√©gia de tokeniza√ß√£o √© a inten√ß√£o dos Bancos Centrais e governos de tokenizar suas moedas fiduci√°rias. Isto j√° est√° em testes na China e o Banco Central do Brasil j√° sinalizou esta inten√ß√£o tamb√©m.</p>

							<p>A tokeniza√ß√£o facilita a transfer√™ncia e o registro da posse de um bem, por conta das caracter√≠sticas de funcionamento da blockchain.</p>
						</CardBody>					
					</Card>
				</Collapse>
			</div>		
		</section>		
	);
  }

  function OqEtoken() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
				<button className="faqbutton" onClick={toggle} style={{ marginBottom: "1rem"}}>
					<h5 style={{marginTop:'20px'}}>Mas, o que √© um token?</h5>
				</button>
				<img onClick={toggle} style={{ cursor:'pointer'}} alt=""src={Vector}></img>
				
				<Collapse isOpen={isOpen}>
				<Card style={{boxShadow:'none', borderRadius:'20px', border:'none',height:'100%'}}>
					<CardBody >
					<p>O token √© um registro digital.</p>
					<p>√â a representa√ß√£o digital de algo que existe no mundo real, ou seja o token representa um ativo base como por exemplo</p>
					<ul>
						<li><p>cotas de uma empresa, </p></li>
						<li><p>um im√≥vel,</p></li>
						<li><p>uma obra de arte, </p></li>
						<li><p>direitos de recebimentos futuros como valores a receber de um youtuber, </p></li>
						<li><p>pontos de fidelidade</p></li>
						<li><p>alugu√©is, e muitos outros exemplos. </p></li>
					</ul>
					<p>Praticamente tudo que existe f√≠sica e digitalmente e tem um valor, pode ser digitalizado na forma de um  token.</p>
					<p>Voc√™ pode criar um token para arrecadar doa√ß√µes com a vantagem de poder fazer isso pelo mundo todo.</p>
					
					</CardBody>					
				</Card>
				</Collapse>
			</div>		
		</section>		
	);
  }

  function AtivosDigitaisl() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
				<button className="faqbutton" onClick={toggle} style={{ marginBottom: "1rem"}}>
					<h5 style={{marginTop:'20px'}}>Ativos digitais podem ser tokenizados?</h5>
				</button>
				<img onClick={toggle} style={{ cursor:'pointer'}} alt=""src={Vector}></img>
				
				<Collapse isOpen={isOpen}>
				<Card style={{boxShadow:'none', borderRadius:'20px', border:'none',height:'100%'}}>
					<CardBody >
					<p>Sim. Itens digitais tamb√©m podem ser tokenizados. </p>					
					</CardBody>					
				</Card>
				</Collapse>
			</div>		
		</section>		
	);
  }

  function OqEnFT() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
				<button className="faqbutton" onClick={toggle} style={{ marginBottom: "1rem"}}>
					<h5 style={{marginTop:'20px'}}>O que √© NFT?</h5>
				</button>
				<img onClick={toggle} style={{ cursor:'pointer'}} alt=""src={Vector}></img>
				
				<Collapse isOpen={isOpen}>
				<Card style={{boxShadow:'none', borderRadius:'20px', border:'none',height:'100%'}}>
					<CardBody >
					<p>NFT quer dizer ‚Äúnon fungeble token‚Äù. Este tipo de token representa um ativo base √öNICO que n√£o √© fung√≠vel. </p>
					
					
					</CardBody>					
				</Card>
				</Collapse>
			</div>		
		</section>		
	);
  }

  function OqAtivoNfungivel() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
				<button className="faqbutton" onClick={toggle} style={{ marginBottom: "1rem"}}>
					<h5 style={{marginTop:'20px'}}>O que √© ativo n√£o fung√≠vel?</h5>
				</button>
				<img onClick={toggle} style={{ cursor:'pointer'}} alt=""src={Vector}></img>
				
				<Collapse isOpen={isOpen}>
				<Card style={{boxShadow:'none', borderRadius:'20px', border:'none', height:'100%'}}>
					<CardBody >
					<p>Uma nota de cem reais √© um ativo fung√≠vel pois outra nota de cem reais pode ser entregue no lugar da primeira pois possui o mesmo valor. J√° um quadro, uma obra de arte, seja ela f√≠sica ou digital √© √∫nica e n√£o pode ser trocada por outra pois n√£o existe uma outra igual. </p>
					
					
					</CardBody>					
				</Card>
				</Collapse>
			</div>		
		</section>		
	);
  }

  function QuemEmitirToken() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
