  function OndeGuardadoToken() {
	const [isOpen, setIsOpen] = useState(false);
	const toggle = () => setIsOpen(!isOpen);
	
	return (
		
		<section class="mb-4">
		  	<div className="FaqDiv" >
				
				<button className="faqbutton" onClick={toggle} style={{ marginBottom: "1rem"}}>
					<h5 style={{marginTop:'20px'}}>Onde fica guardado o meu token?</h5>
